import { useEffect, useState } from "react";
import axios from "axios";

function ReadBooks() {
    const [readBooks, setReadBooks] = useState([]);
    const [reviews, setReviews] = useState({}); // reviewText dla każdej książki
    const token = localStorage.getItem("token");

    const fetchReadBooks = async () => {
        try {
            const res = await axios.get("http://localhost:8080/api/library/read", {
                headers: { "x-access-token": token },
            });
            setReadBooks(res.data.data);

            // Inicjalizacja reviews - przypisz aktualne opinie do stanu
            const initialReviews = {};
            res.data.data.forEach((book) => {
                initialReviews[book._id] = book.reviewText || "";
            });
            setReviews(initialReviews);
        } catch (err) {
            console.error("Błąd pobierania przeczytanych książek:", err);
        }
    };

    useEffect(() => {
        fetchReadBooks();
    }, []);

    const handleReviewChange = (bookId, value) => {
        setReviews((prev) => ({ ...prev, [bookId]: value }));
    };

    const submitReview = async (readBookId) => {
        console.log("Wysyłam opinię:", {
            readBookId,
            reviewText: reviews[readBookId]
        });
        try {
            await axios.post(
                `http://localhost:8080/api/library/read/${readBookId}/opinion`,
                { reviewText: reviews[readBookId] },
                { headers: { "x-access-token": token } }
            );
            alert("Opinia została dodana!");
            fetchReadBooks(); // odśwież listę po aktualizacji
        } catch (error) {
            console.error("Błąd dodawania opinii:", error.response?.data || error.message);
            alert("Nie udało się dodać opinii.");
        }
    };

    return (
        <div>
            <h2>Przeczytane książki</h2>
            <ul>
                {readBooks.map((book) => (
                    <li key={book._id} style={{ marginBottom: "2rem", borderBottom: "1px solid #ccc", paddingBottom: "1rem" }}>
                        <h3>{book.bookTitle}</h3>
                        <p><strong>Autor:</strong> {book.author}</p>
                        {book.dateRead && <p><strong>Data przeczytania:</strong> {new Date(book.dateRead).toLocaleDateString()}</p>}
                        {book.rating && <p><strong>Ocena:</strong> {book.rating}</p>}
                        {book.reviewText && <p><strong>Opinia:</strong> {book.reviewText}</p>}

                        <textarea
                            placeholder="Dodaj lub edytuj swoją opinię..."
                            value={reviews[book._id] || ""}
                            onChange={(e) => handleReviewChange(book._id, e.target.value)}
                            rows={3}
                            style={{ width: "100%", marginTop: "0.5rem" }}
                        />
                        <button onClick={() => submitReview(book._id)} style={{ marginTop: "0.5rem" }}>
                            Zapisz opinię
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ReadBooks;
