import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "./styles.module.css";
import Users from "./Users";

// Wewnętrzny komponent Books
function Books() {
  const [books, setBooks] = useState([]);
  const token = localStorage.getItem("token");

  const fetchBooks = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/library", {
        headers: { "x-access-token": token },
      });
      setBooks(res.data.data);
    } catch (error) {
      console.error("Błąd pobierania książek:", error);
    }
  };

  const markAsRead = async (bookId) => {
    try {
      await axios.post(
        `http://localhost:8080/api/library/read/${bookId}`,
        {},
        { headers: { "x-access-token": token } }
      );
      alert("Dodano do listy przeczytanych książek!");
    } catch (error) {
      console.error("Błąd dodawania do przeczytanych:", error.response?.data || error.message);
      alert(`Nie udało się dodać do przeczytanych: ${error.response?.data?.message || error.message}`);
    }
  };

  const deleteBook = async (bookId) => {
    try {
      await axios.delete(`http://localhost:8080/api/library/${bookId}`, {
        headers: { "x-access-token": token },
      });
      alert("Książka została usunięta.");
      fetchBooks();
    } catch (error) {
      console.error("Błąd usuwania książki:", error.response?.data || error.message);
      alert(`Nie udało się usunąć książki: ${error.response?.data?.message || error.message}`);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="books-container">
      <h2>Książki</h2>
      <div className="books-list">
        {books.map((book) => (
          <div className="book-card" key={book._id}>
            <h3 className="book-title">{book.title}</h3>
            <p className="book-author"><em>{book.author}</em></p>
            {book.year && <p className="book-year">Rok wydania: {book.year}</p>}
            {book.description && <p className="book-description">{book.description}</p>}
            <button className="read-button" onClick={() => markAsRead(book._id)}>
              Dodaj do przeczytanych
            </button>
            <button className="delete-button" onClick={() => deleteBook(book._id)}>
              Usuń książkę
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

// Komponent Main
const Main = () => {
  const [dane, setDane] = useState([]);
  const [showUsers, setShowUsers] = useState(false);
  const [showBooks, setShowBooks] = useState(false);
  const [accountDetails, setAccountDetails] = useState(null);
  const [message, setMessage] = useState("");

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const navigate = useNavigate();

  const goToAddBook = () => {
    navigate("/add-book");
  };

  const goToReadBooks = () => {
    navigate("/read-books");
  };

  const goToBooks = () => {
    setShowBooks(true);
    setShowUsers(false);
    setAccountDetails(null);
    setMessage("Lista książek:");
  };

  const handleGetUsers = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const config = {
          method: "get",
          url: "http://localhost:8080/api/users",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        };
        const { data: res } = await axios(config);
        setDane(res.data);
        setMessage(res.message || "Lista użytkowników:");
        setShowUsers(true);
        setShowBooks(false);
        setAccountDetails(null);
      } catch (error) {
        if (error.response && error.response.status >= 400 && error.response.status <= 500) {
          localStorage.removeItem("token");
          window.location.reload();
        }
      }
    }
  };

  const handleGetAccountDetails = async () => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const config = {
          method: "get",
          url: "http://localhost:8080/api/users/details",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        };
        const { data: res } = await axios(config);
        setAccountDetails(res.data);
        setMessage(res.message || "Szczegóły konta:");
        setShowUsers(false);
        setShowBooks(false);
        setDane([]);
      } catch (error) {
        console.error("Błąd w handleGetAccountDetails:", error.response || error.message);
        if (error.response && error.response.status >= 400 && error.response.status <= 500) {
          localStorage.removeItem("token");
          window.location.reload();
        }
      }
    }
  };

  const handleDeleteAccount = async () => {
    const confirmDelete = window.confirm("Czy na pewno chcesz usunąć swoje konto?");
    if (!confirmDelete) return;

    const token = localStorage.getItem("token");
    if (token) {
      try {
        const config = {
          method: "delete",
          url: "http://localhost:8080/api/users/delete",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        };
        const { data: res } = await axios(config);
        alert(res.message || "Konto zostało usunięte.");
        localStorage.removeItem("token");
        window.location.reload();
      } catch (error) {
        if (error.response && error.response.status >= 400 && error.response.status <= 500) {
          alert("Błąd przy usuwaniu konta.");
          localStorage.removeItem("token");
          window.location.reload();
        }
      }
    }
  };

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>Dziennik lektur</h1>
        <div>
          <button className={styles.white_btn} onClick={handleGetUsers}>Użytkownicy</button>
          <button className={styles.white_btn} onClick={handleGetAccountDetails}>Szczegóły konta</button>
          <button className={styles.white_btn} onClick={handleDeleteAccount}>Usuń konto</button>
          <button className={styles.white_btn} onClick={handleLogout}>Wyloguj się</button>
          <button className={styles.white_btn} onClick={goToBooks}>Wyświetl wszystkie książki</button>
          <button className={styles.white_btn} onClick={goToAddBook}>Dodaj książkę</button>
          <button className={styles.white_btn} onClick={goToReadBooks}>Wyświetl przeczytane książki</button>
        </div>
      </nav>

      {message && <h2>{message}</h2>}

      {showUsers && dane.length > 0 && <Users users={dane} />}
      {showUsers && dane.length === 0 && <p>Brak użytkowników do wyświetlenia.</p>}

      {accountDetails && (
        <div>
          <ul>
            <li><strong>First Name:</strong> {accountDetails.firstName}</li>
            <li><strong>Last Name:</strong> {accountDetails.lastName}</li>
            <li><strong>Email:</strong> {accountDetails.email}</li>
          </ul>
        </div>
      )}

      {showBooks && <Books />}
    </div>
  );
};

export default Main;
