import { useState } from "react";
import axios from "axios";

function AddBook({ onBookAdded }) {
  const [newBook, setNewBook] = useState({ title: "", author: "", rating: "", review: "" });
  const token = localStorage.getItem("token");

  const handleAddBook = async () => {
    try {
      await axios.post(
        "http://localhost:8080/api/readBooks/add",
        {
          title: newBook.title,
          author: newBook.author,
          rating: newBook.rating,
          review: newBook.review,
        },
        { headers: { "x-access-token": token } }
      );
      setNewBook({ title: "", author: "", rating: "", review: "" });
      alert("Książka dodana!");
      if (onBookAdded) onBookAdded(); // opcjonalnie odśwież listę
    } catch (error) {
      console.error("Błąd dodawania książki:", error);
    }
  };

  return (
    <div>
      <h2>Dodaj książkę do przeczytanych</h2>
      <input
        type="text"
        placeholder="Tytuł"
        value={newBook.title}
        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Autor"
        value={newBook.author}
        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
        required
      />
      <input
        type="number"
        placeholder="Ocena (1-5)"
        value={newBook.rating}
        onChange={(e) => setNewBook({ ...newBook, rating: e.target.value })}
        min="1"
        max="5"
      />
      <textarea
        placeholder="Recenzja"
        value={newBook.review}
        onChange={(e) => setNewBook({ ...newBook, review: e.target.value })}
      />
      <button onClick={handleAddBook}>Dodaj książkę</button>
    </div>
  );
}

export default AddBook;
