import { useState } from "react";
import axios from "axios";
import styles from "./styles.module.css";
import Users from "./Users";

const Main = () => {
    const [dane, setDane] = useState([]);         // lista użytkowników
    const [showUsers, setShowUsers] = useState(false); // flaga widoczności

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const handleGetUsers = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/users',
                    headers: {
                        'Content-Type': 'application/json',
                        'x-access-token': token
                    }
                };
                const { data: res } = await axios(config);
                setDane(res.data);     // zapisujemy dane
                setShowUsers(true);    // pokazujemy listę
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    localStorage.removeItem("token");
                    window.location.reload();
                }
            }
        }
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>MySite</h1>
                <div>
                    <button className={styles.white_btn} onClick={handleGetUsers}>
                        Get Users
                    </button>
                    <button className={styles.white_btn} onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            </nav>

            {showUsers && dane.length > 0 ? (
                <Users users={dane} />
            ) : showUsers ? (
                <p>No users found.</p>
            ) : null}
        </div>
    );
};

export default Main;
