import { useEffect, useState } from "react";
import axios from "axios";

function ReadBooks() {
  const [readBooks, setReadBooks] = useState([]);
  const [opinions, setOpinions] = useState({}); // opinie wpisywane przez użytkownika
  const token = localStorage.getItem("token");

  const fetchReadBooks = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/library/read", {
        headers: { "x-access-token": token },
      });
      setReadBooks(res.data.data);
    } catch (err) {
      console.error("Błąd pobierania przeczytanych książek:", err);
    }
  };

  useEffect(() => {
    fetchReadBooks();
  }, []);

  const handleOpinionChange = (bookId, value) => {
    setOpinions((prev) => ({ ...prev, [bookId]: value }));
  };

  const submitOpinion = async (bookId) => {
    try {
      await axios.post(
        `http://localhost:8080/api/library/read/${bookId}/opinion`,
        { opinion: opinions[bookId] },
        { headers: { "x-access-token": token } }
      );
      alert("Opinia została dodana!");
      setOpinions((prev) => ({ ...prev, [bookId]: "" }));
      fetchReadBooks();
    } catch (error) {
      console.error("Błąd dodawania opinii:", error.response?.data || error.message);
      alert("Nie udało się dodać opinii.");
    }
  };

  return (
    <div>
      <h2>Przeczytane książki</h2>
      <ul>
        {readBooks.map((book) => (
          <li key={book._id} style={{ marginBottom: "2rem", borderBottom: "1px solid #ccc", paddingBottom: "1rem" }}>
            <h3>{book.title}</h3>
            <p><strong>Autor:</strong> {book.author}</p>
            {book.year && <p><strong>Rok wydania:</strong> {book.year}</p>}
            {book.rating && <p><strong>Ocena:</strong> {book.rating}</p>}
            {book.description && <p><strong>Opis:</strong> {book.description}</p>}

            <textarea
              placeholder="Dodaj swoją opinię..."
              value={opinions[book._id] || ""}
              onChange={(e) => handleOpinionChange(book._id, e.target.value)}
              rows={3}
              style={{ width: "100%", marginTop: "0.5rem" }}
            />
            <button onClick={() => submitOpinion(book._id)} style={{ marginTop: "0.5rem" }}>
              Dodaj opinię
            </button>

            {/* Jeśli masz już opinie, możesz je tutaj wyświetlić */}
            {book.opinions && book.opinions.length > 0 && (
              <div style={{ marginTop: "1rem" }}>
                <strong>Opinie:</strong>
                <ul>
                  {book.opinions.map((op, idx) => (
                    <li key={idx}>{op}</li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ReadBooks;
