import { useEffect, useState } from "react";
import axios from "axios";

function ReadBooks() {
  const [readBooks, setReadBooks] = useState([]);
  const token = localStorage.getItem("token");

  const fetchReadBooks = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/library/read", {
        headers: { "x-access-token": token },
      });
      setReadBooks(res.data.data);
    } catch (err) {
      console.error("Błąd pobierania przeczytanych książek:", err);
    }
  };

  useEffect(() => {
    fetchReadBooks();
  }, []);

  return (
    <div className="books-container">
      <h2>Książki</h2>
      <div className="books-list">
        {books.map((book) => (
          <div className="book-card" key={book._id}>
            <h3 className="book-title">{book.title}</h3>
            <p className="book-author"><em>{book.author}</em></p>
            {book.year && <p className="book-year">Rok wydania: {book.year}</p>}
            {book.description && <p className="book-description">{book.description}</p>}
            <button className="read-button">Dodaj do przeczytanych</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReadBooks;
